export const useAuthorStore = defineStore('authorStore', {
  state: (): AuthorStoreState => ({
    activeID: null,
    cache: {},
    list: [] as Author[],
    retrievalTimestamp: 0,
    updateKey: 0,
    storeUpdateTimeout: null,
  }),
  actions: {
    emitFormUpdateEvent(id: string): void {
      window.dispatchEvent(new CustomEvent("form.update.author", { detail: { id } }));
    },
    emitStoreUpdateEvent(): void {
      // Clear existing timeout
      if (this.storeUpdateTimeout !== null) {
        clearTimeout(this.storeUpdateTimeout)
      }

      // Set new timeout
      this.storeUpdateTimeout = setTimeout(() => {
        window.dispatchEvent(new CustomEvent("store.update.author"));
        this.storeUpdateTimeout = null
      }, 1500)
    },
    async activate(author: Author): Promise<void> {
      // Set active
      this.activeID = author.id
    },
    add(author: Author): void {
      const id = author.id
      if (typeof id === 'string' && !this.contains(id)) {
        this.list.push(author)

        // Set active
        if (this.active === null) {
          this.activeID = author.id
        }
      } else {
        this.reinsert(author)
      }
      this.updateKey += 1;
    },
    async delete(author: Author): Promise<void> {
      await AuthorTransporter.Delete(author).then(() => {
        this.remove(author)
      }).finally(() => {
        this.emitStoreUpdateEvent()
      });
    },
    async deleteById(id: string): Promise<void> {
      const author = (this.list as Author[]).find((x: Author) => x.id === id)
      if (author !== undefined) {
        await this.delete(author)
      } else {
        throw new Error('Could not find Author with id: ' + id)
      }
    },
    contains(id: string): boolean {
      return (this.list as Author[]).some((x: Author) => x.id === id)
    },
    async create(author: Author): Promise<Author> {
      // Create author
      const item = await AuthorTransporter.Create({
      }, author)

      // Reinsert author
      this.reinsert(item)

      // Activate author
      if (this.active === null) {
        await this.activate(item)
      }
      
      this.emitStoreUpdateEvent()

      return item
    },
    get(id: string): Author | null {
      return (this.list as Author[]).find((x: Author) => x.id === id) || null
    },
    reinsert(author: Author): void {
      const index = (this.list as Author[]).findIndex((x: Author) => x.id === author.id)
      if (index !== -1) {
        this.list = [
          ...this.list.slice(0, index),
          author,
          ...this.list.slice(index + 1),
        ]
      } else {
        this.add(author)
      }
      this.updateKey += 1;
    },
    remove({ id }: Author): void {
      const index = (this.list as Author[]).findIndex((x: Author) => x.id === id)
      if (index !== -1) {
        this.list = [
          ...this.list.slice(0, index),
          ...this.list.slice(index + 1),
        ]
      }
      this.updateKey += 1;
    },
    async retrieve(id: string, force = false): Promise<Author> {
      // Check if a refresh is required
      if (!force && !this.refreshRequired) {
        const author = this.get(id)
        if (author !== null) {
          return author
        }
      }

      // Get author
      const author = await AuthorTransporter.Get({
        id,
      }).then(async (entity) => {
        this.reinsert(entity)

        // Activate author
        await this.activate(entity)

        return entity
      })

      return author
    },
    async retrieveAll(force = false): Promise<{
      active: string | null
      items: Author[]
    }> {
      // Check if a refresh is required
      if (!force && !this.refreshRequired) {
        return {
          active: this.activeID,
          items: this.list as Author[],
        }
      }

      // Clear list
      while (this.list.length > 0) {
        this.list.pop()
      }

      // Retrieve list
      await AuthorTransporter.GetAll({
      }).then(async (list) => {
        list.forEach((author) => {
          this.reinsert(author)
        })

        // Activate author
        if (list.length > 0) {
          if (this.activeID === null) {
            await this.activate(list[0])
          }
        }
      })

      // Set retrieval timestamp
      this.retrievalTimestamp = Date.now()

      return {
        active: this.activeID,
        items: this.list as Author[],
      }
    },
    async retrievePaginated(pagination: Pagination, force = false): Promise<{
      pagination: Pagination;
      items: Author[];
    }> {
      // Check pagination
      let newPagination = new Pagination(pagination).deepCopy();

      // Get cachekey
      const cacheKey = pagination.toCacheKey();

      // Check if a refresh is required
      if (!force && this.cache[cacheKey]) {
        const cached = this.cache[cacheKey]
        return {
          pagination: cached.pagination.deepCopy(),
          items: cached.ids.map((id: string) => this.get(id) as Author),
        };
      }

      // Retrieve list
      const items = await AuthorTransporter.GetPaginated(pagination).then(async ({items, pagination }) => {
        items.forEach((author) => {
          this.reinsert(author);
        });

        // Set new pagination
        newPagination = pagination.deepCopy();

        return items;
      });

      // Set cache
      this.cache[cacheKey] = {
        ids: items.map((x: Author) => x.id),
        pagination: newPagination,
        retrievalTimestamp: Date.now(),
      };

      // Set retrieval timestamp
      this.retrievalTimestamp = Date.now();

      return {
        pagination: newPagination,
        items,
      };
    },
    async query(pagination: Pagination, queries: Query[], force = false): Promise<{
      pagination: Pagination;
      items: Author[];
    }> {
      // Check pagination
      let newPagination = new Pagination(pagination).deepCopy();

      // Get cachekey
      const cacheKey = paginationAndQueriesToCacheKey(pagination, queries);

      // Check if a refresh is required
      if (!force && this.cache[cacheKey]) {
        const cached = this.cache[cacheKey]
        return {
          pagination: cached.pagination.deepCopy(),
          items: cached.ids.map((id: string) => this.get(id) as Author),
        };
      }

      // Retrieve list
      const items = await AuthorTransporter.Query(
        pagination,
        queries,
      ).then(async ({ items, pagination }) => {
        items.forEach((author) => {
          this.reinsert(author);
        });

        // Set new pagination
        newPagination = pagination.deepCopy();;

        return items
      });

      // Set cache
      this.cache[cacheKey] = {
        ids: items.map((x: Author) => x.id),
        pagination: newPagination.deepCopy(),
        retrievalTimestamp: Date.now(),
      };

      // Set retrieval timestamp
      this.retrievalTimestamp = Date.now();

      return {
        pagination: newPagination,
        items: items,
      };
    },
    async search(
      
      pagination: Pagination,
      search: string,
      force = false,
    ): Promise<{
      pagination: Pagination;
      items: Author[];
    }> {
      // Check pagination
      let newPagination = new Pagination(pagination).deepCopy();

      // Get cachekey
      const cacheKey = search.replaceAll(" ", "_") + '-' + pagination.toCacheKey();

      // Check if a refresh is required
      if (!force && this.cache[cacheKey]) {
        const cached = this.cache[cacheKey]
        return {
          items: cached.ids.map(
            (id: string) => this.get(id) as Author,
          ),
          pagination: cached.pagination.deepCopy(),
        };
      }

      // Retrieve list
      const items = await AuthorTransporter.Search(
        pagination,
        search,
      ).then(async ({ items, pagination }) => {
        items.forEach((author) => {
          this.reinsert(author);
        });

        // Set new pagination
        newPagination = pagination.deepCopy();;

        return items
      });

      // Set cache
      this.cache[cacheKey] = {
        ids: items.map((x: Author) => x.id),
        pagination: newPagination.deepCopy(),
        retrievalTimestamp: Date.now(),
      };

      // Set retrieval timestamp
      this.retrievalTimestamp = Date.now();

      return {
        items: items,
        pagination: newPagination,
      };
    },
    async update(author: Author): Promise<Author> {
      // Update author
      const item = await AuthorTransporter.Update(author)

      // Reinsert author
      this.reinsert(item)

      // Activate author
      if (this.active === null) {
        await this.activate(item)
      }
      
      this.emitStoreUpdateEvent()

      return item
    },
  },
  getters: {
    active(): Author | null {
      if (this.activeID === null) {
        return null
      }

      const author = (this.list as Author[]).find((x: Author) => x.id === this.activeID)
      if (author === undefined) {
        return null
      }

      return author
    },
    refreshRequired(): boolean {
      return (
        this.retrievalTimestamp === 0 ||
        this.retrievalTimestamp < Date.now() - 30 * 60000 ||
        this.list.length === 0
      )
    },
  },
})

