import { defineStore } from 'pinia'
import { Purchase } from './interface.gen'
import type { PurchaseStore, PurchaseStoreState } from './store.gen.d'
import { Pagination, paginationAndQueriesToCacheKey } from "../../general/pagination";
import { Query } from "../../general/query";
import { PurchaseTransporter } from './transport.gen'

/// ///////////////////////////////////////////////////////////////////////////////////////////////////
