export const usePurchaseStore = defineStore('purchaseStore', {
  state: (): PurchaseStoreState => ({
    activeID: null,
    cache: {},
    list: [] as Purchase[],
    retrievalTimestamp: 0,
    updateKey: 0,
    storeUpdateTimeout: null,
  }),
  actions: {
    emitFormUpdateEvent(id: string): void {
      window.dispatchEvent(new CustomEvent("form.update.purchase", { detail: { id } }));
    },
    emitStoreUpdateEvent(): void {
      // Clear existing timeout
      if (this.storeUpdateTimeout !== null) {
        clearTimeout(this.storeUpdateTimeout)
      }

      // Set new timeout
      this.storeUpdateTimeout = setTimeout(() => {
        window.dispatchEvent(new CustomEvent("store.update.purchase"));
        this.storeUpdateTimeout = null
      }, 1500)
    },
    async activate(purchase: Purchase): Promise<void> {
      // Set active
      this.activeID = purchase.id
    },
    add(purchase: Purchase): void {
      const id = purchase.id
      if (typeof id === 'string' && !this.contains(id)) {
        this.list.push(purchase)

        // Set active
        if (this.active === null) {
          this.activeID = purchase.id
        }
      } else {
        this.reinsert(purchase)
      }
      this.updateKey += 1;
    },
    async delete(purchase: Purchase): Promise<void> {
      await PurchaseTransporter.Delete(purchase).then(() => {
        this.remove(purchase)
      }).finally(() => {
        this.emitStoreUpdateEvent()
      });
    },
    async deleteById(id: string): Promise<void> {
      const purchase = (this.list as Purchase[]).find((x: Purchase) => x.id === id)
      if (purchase !== undefined) {
        await this.delete(purchase)
      } else {
        throw new Error('Could not find Purchase with id: ' + id)
      }
    },
    contains(id: string): boolean {
      return (this.list as Purchase[]).some((x: Purchase) => x.id === id)
    },
    async create(userId: string, purchase: Purchase): Promise<Purchase> {
      // Create purchase
      const item = await PurchaseTransporter.Create({
        user_id: userId,
      }, purchase)

      // Reinsert purchase
      this.reinsert(item)

      // Activate purchase
      if (this.active === null) {
        await this.activate(item)
      }
      
      this.emitStoreUpdateEvent()

      return item
    },
    get(id: string): Purchase | null {
      return (this.list as Purchase[]).find((x: Purchase) => x.id === id) || null
    },
    reinsert(purchase: Purchase): void {
      const index = (this.list as Purchase[]).findIndex((x: Purchase) => x.id === purchase.id)
      if (index !== -1) {
        this.list = [
          ...this.list.slice(0, index),
          purchase,
          ...this.list.slice(index + 1),
        ]
      } else {
        this.add(purchase)
      }
      this.updateKey += 1;
    },
    remove({ id }: Purchase): void {
      const index = (this.list as Purchase[]).findIndex((x: Purchase) => x.id === id)
      if (index !== -1) {
        this.list = [
          ...this.list.slice(0, index),
          ...this.list.slice(index + 1),
        ]
      }
      this.updateKey += 1;
    },
    async retrieve(userId: string, id: string, force = false): Promise<Purchase> {
      // Check if a refresh is required
      if (!force && !this.refreshRequired) {
        const purchase = this.get(id)
        if (purchase !== null) {
          return purchase
        }
      }

      // Get purchase
      const purchase = await PurchaseTransporter.Get({
        user_id: userId,
        id,
      }).then(async (entity) => {
        this.reinsert(entity)

        // Activate purchase
        await this.activate(entity)

        return entity
      })

      return purchase
    },
    async retrieveAll(userId: string, force = false): Promise<{
      active: string | null
      items: Purchase[]
    }> {
      // Check if a refresh is required
      if (!force && !this.refreshRequired) {
        return {
          active: this.activeID,
          items: this.list as Purchase[],
        }
      }

      // Clear list
      while (this.list.length > 0) {
        this.list.pop()
      }

      // Retrieve list
      await PurchaseTransporter.GetAll({
        user_id: userId,
      }).then(async (list) => {
        list.forEach((purchase) => {
          this.reinsert(purchase)
        })

        // Activate purchase
        if (list.length > 0) {
          if (this.activeID === null) {
            await this.activate(list[0])
          }
        }
      })

      // Set retrieval timestamp
      this.retrievalTimestamp = Date.now()

      return {
        active: this.activeID,
        items: this.list as Purchase[],
      }
    },
    async retrievePaginated(userId: string, pagination: Pagination, force = false): Promise<{
      pagination: Pagination;
      items: Purchase[];
    }> {
      // Check pagination
      let newPagination = new Pagination(pagination).deepCopy();

      // Get cachekey
      const cacheKey = pagination.toCacheKey();

      // Check if a refresh is required
      if (!force && this.cache[cacheKey]) {
        const cached = this.cache[cacheKey]
        return {
          pagination: cached.pagination.deepCopy(),
          items: cached.ids.map((id: string) => this.get(id) as Purchase),
        };
      }

      // Retrieve list
      const items = await PurchaseTransporter.GetPaginated({
        user_id: userId,
      },pagination).then(async ({items, pagination }) => {
        items.forEach((purchase) => {
          this.reinsert(purchase);
        });

        // Set new pagination
        newPagination = pagination.deepCopy();

        return items;
      });

      // Set cache
      this.cache[cacheKey] = {
        ids: items.map((x: Purchase) => x.id),
        pagination: newPagination,
        retrievalTimestamp: Date.now(),
      };

      // Set retrieval timestamp
      this.retrievalTimestamp = Date.now();

      return {
        pagination: newPagination,
        items,
      };
    },
    async query(userId: string, pagination: Pagination, queries: Query[], force = false): Promise<{
      pagination: Pagination;
      items: Purchase[];
    }> {
      // Check pagination
      let newPagination = new Pagination(pagination).deepCopy();

      // Get cachekey
      const cacheKey = paginationAndQueriesToCacheKey(pagination, queries);

      // Check if a refresh is required
      if (!force && this.cache[cacheKey]) {
        const cached = this.cache[cacheKey]
        return {
          pagination: cached.pagination.deepCopy(),
          items: cached.ids.map((id: string) => this.get(id) as Purchase),
        };
      }

      // Retrieve list
      const items = await PurchaseTransporter.Query({
        user_id: userId,
      },
        pagination,
        queries,
      ).then(async ({ items, pagination }) => {
        items.forEach((purchase) => {
          this.reinsert(purchase);
        });

        // Set new pagination
        newPagination = pagination.deepCopy();;

        return items
      });

      // Set cache
      this.cache[cacheKey] = {
        ids: items.map((x: Purchase) => x.id),
        pagination: newPagination.deepCopy(),
        retrievalTimestamp: Date.now(),
      };

      // Set retrieval timestamp
      this.retrievalTimestamp = Date.now();

      return {
        pagination: newPagination,
        items: items,
      };
    },
    async search(
      userId: string, 
      pagination: Pagination,
      search: string,
      force = false,
    ): Promise<{
      pagination: Pagination;
      items: Purchase[];
    }> {
      // Check pagination
      let newPagination = new Pagination(pagination).deepCopy();

      // Get cachekey
      const cacheKey = search.replaceAll(" ", "_") + '-' + pagination.toCacheKey();

      // Check if a refresh is required
      if (!force && this.cache[cacheKey]) {
        const cached = this.cache[cacheKey]
        return {
          items: cached.ids.map(
            (id: string) => this.get(id) as Purchase,
          ),
          pagination: cached.pagination.deepCopy(),
        };
      }

      // Retrieve list
      const items = await PurchaseTransporter.Search({
        user_id: userId,
      },
        pagination,
        search,
      ).then(async ({ items, pagination }) => {
        items.forEach((purchase) => {
          this.reinsert(purchase);
        });

        // Set new pagination
        newPagination = pagination.deepCopy();;

        return items
      });

      // Set cache
      this.cache[cacheKey] = {
        ids: items.map((x: Purchase) => x.id),
        pagination: newPagination.deepCopy(),
        retrievalTimestamp: Date.now(),
      };

      // Set retrieval timestamp
      this.retrievalTimestamp = Date.now();

      return {
        items: items,
        pagination: newPagination,
      };
    },
    async update(purchase: Purchase): Promise<Purchase> {
      // Update purchase
      const item = await PurchaseTransporter.Update(purchase)

      // Reinsert purchase
      this.reinsert(item)

      // Activate purchase
      if (this.active === null) {
        await this.activate(item)
      }
      
      this.emitStoreUpdateEvent()

      return item
    },
  },
  getters: {
    active(): Purchase | null {
      if (this.activeID === null) {
        return null
      }

      const purchase = (this.list as Purchase[]).find((x: Purchase) => x.id === this.activeID)
      if (purchase === undefined) {
        return null
      }

      return purchase
    },
    refreshRequired(): boolean {
      return (
        this.retrievalTimestamp === 0 ||
        this.retrievalTimestamp < Date.now() - 30 * 60000 ||
        this.list.length === 0
      )
    },
  },
})

// Export a function to create a new instance of the store
export function createPurchaseStore(): PurchaseStore {
  const store = usePurchaseStore()
  // Run the initializeStore action when the store is created
  store.initializeStore()
  return store as unknown as PurchaseStore // TODO: Fix this
}
