export const useTagStore = defineStore('tagStore', {
  state: (): TagStoreState => ({
    activeID: null,
    cache: {},
    list: [] as Tag[],
    retrievalTimestamp: 0,
    updateKey: 0,
    storeUpdateTimeout: null,
  }),
  actions: {
    emitFormUpdateEvent(id: string): void {
      window.dispatchEvent(new CustomEvent("form.update.tag", { detail: { id } }));
    },
    emitStoreUpdateEvent(): void {
      // Clear existing timeout
      if (this.storeUpdateTimeout !== null) {
        clearTimeout(this.storeUpdateTimeout)
      }

      // Set new timeout
      this.storeUpdateTimeout = setTimeout(() => {
        window.dispatchEvent(new CustomEvent("store.update.tag"));
        this.storeUpdateTimeout = null
      }, 1500)
    },
    async activate(tag: Tag): Promise<void> {
      // Set active
      this.activeID = tag.id
    },
    add(tag: Tag): void {
      const id = tag.id
      if (typeof id === 'string' && !this.contains(id)) {
        this.list.push(tag)

        // Set active
        if (this.active === null) {
          this.activeID = tag.id
        }
      } else {
        this.reinsert(tag)
      }
      this.updateKey += 1;
    },
    async delete(tag: Tag): Promise<void> {
      await TagTransporter.Delete(tag).then(() => {
        this.remove(tag)
      }).finally(() => {
        this.emitStoreUpdateEvent()
      });
    },
    async deleteById(id: string): Promise<void> {
      const tag = (this.list as Tag[]).find((x: Tag) => x.id === id)
      if (tag !== undefined) {
        await this.delete(tag)
      } else {
        throw new Error('Could not find Tag with id: ' + id)
      }
    },
    contains(id: string): boolean {
      return (this.list as Tag[]).some((x: Tag) => x.id === id)
    },
    async create(tag: Tag): Promise<Tag> {
      // Create tag
      const item = await TagTransporter.Create({
      }, tag)

      // Reinsert tag
      this.reinsert(item)

      // Activate tag
      if (this.active === null) {
        await this.activate(item)
      }
      
      this.emitStoreUpdateEvent()

      return item
    },
    get(id: string): Tag | null {
      return (this.list as Tag[]).find((x: Tag) => x.id === id) || null
    },
    reinsert(tag: Tag): void {
      const index = (this.list as Tag[]).findIndex((x: Tag) => x.id === tag.id)
      if (index !== -1) {
        this.list = [
          ...this.list.slice(0, index),
          tag,
          ...this.list.slice(index + 1),
        ]
      } else {
        this.add(tag)
      }
      this.updateKey += 1;
    },
    remove({ id }: Tag): void {
      const index = (this.list as Tag[]).findIndex((x: Tag) => x.id === id)
      if (index !== -1) {
        this.list = [
          ...this.list.slice(0, index),
          ...this.list.slice(index + 1),
        ]
      }
      this.updateKey += 1;
    },
    async retrieve(id: string, force = false): Promise<Tag> {
      // Check if a refresh is required
      if (!force && !this.refreshRequired) {
        const tag = this.get(id)
        if (tag !== null) {
          return tag
        }
      }

      // Get tag
      const tag = await TagTransporter.Get({
        id,
      }).then(async (entity) => {
        this.reinsert(entity)

        // Activate tag
        await this.activate(entity)

        return entity
      })

      return tag
    },
    async retrieveAll(force = false): Promise<{
      active: string | null
      items: Tag[]
    }> {
      // Check if a refresh is required
      if (!force && !this.refreshRequired) {
        return {
          active: this.activeID,
          items: this.list as Tag[],
        }
      }

      // Clear list
      while (this.list.length > 0) {
        this.list.pop()
      }

      // Retrieve list
      await TagTransporter.GetAll({
      }).then(async (list) => {
        list.forEach((tag) => {
          this.reinsert(tag)
        })

        // Activate tag
        if (list.length > 0) {
          if (this.activeID === null) {
            await this.activate(list[0])
          }
        }
      })

      // Set retrieval timestamp
      this.retrievalTimestamp = Date.now()

      return {
        active: this.activeID,
        items: this.list as Tag[],
      }
    },
    async retrievePaginated(pagination: Pagination, force = false): Promise<{
      pagination: Pagination;
      items: Tag[];
    }> {
      // Check pagination
      let newPagination = new Pagination(pagination).deepCopy();

      // Get cachekey
      const cacheKey = pagination.toCacheKey();

      // Check if a refresh is required
      if (!force && this.cache[cacheKey]) {
        const cached = this.cache[cacheKey]
        return {
          pagination: cached.pagination.deepCopy(),
          items: cached.ids.map((id: string) => this.get(id) as Tag),
        };
      }

      // Retrieve list
      const items = await TagTransporter.GetPaginated(pagination).then(async ({items, pagination }) => {
        items.forEach((tag) => {
          this.reinsert(tag);
        });

        // Set new pagination
        newPagination = pagination.deepCopy();

        return items;
      });

      // Set cache
      this.cache[cacheKey] = {
        ids: items.map((x: Tag) => x.id),
        pagination: newPagination,
        retrievalTimestamp: Date.now(),
      };

      // Set retrieval timestamp
      this.retrievalTimestamp = Date.now();

      return {
        pagination: newPagination,
        items,
      };
    },
    async query(pagination: Pagination, queries: Query[], force = false): Promise<{
      pagination: Pagination;
      items: Tag[];
    }> {
      // Check pagination
      let newPagination = new Pagination(pagination).deepCopy();

      // Get cachekey
      const cacheKey = paginationAndQueriesToCacheKey(pagination, queries);

      // Check if a refresh is required
      if (!force && this.cache[cacheKey]) {
        const cached = this.cache[cacheKey]
        return {
          pagination: cached.pagination.deepCopy(),
          items: cached.ids.map((id: string) => this.get(id) as Tag),
        };
      }

      // Retrieve list
      const items = await TagTransporter.Query(
        pagination,
        queries,
      ).then(async ({ items, pagination }) => {
        items.forEach((tag) => {
          this.reinsert(tag);
        });

        // Set new pagination
        newPagination = pagination.deepCopy();;

        return items
      });

      // Set cache
      this.cache[cacheKey] = {
        ids: items.map((x: Tag) => x.id),
        pagination: newPagination.deepCopy(),
        retrievalTimestamp: Date.now(),
      };

      // Set retrieval timestamp
      this.retrievalTimestamp = Date.now();

      return {
        pagination: newPagination,
        items: items,
      };
    },
    async search(
      
      pagination: Pagination,
      search: string,
      force = false,
    ): Promise<{
      pagination: Pagination;
      items: Tag[];
    }> {
      // Check pagination
      let newPagination = new Pagination(pagination).deepCopy();

      // Get cachekey
      const cacheKey = search.replaceAll(" ", "_") + '-' + pagination.toCacheKey();

      // Check if a refresh is required
      if (!force && this.cache[cacheKey]) {
        const cached = this.cache[cacheKey]
        return {
          items: cached.ids.map(
            (id: string) => this.get(id) as Tag,
          ),
          pagination: cached.pagination.deepCopy(),
        };
      }

      // Retrieve list
      const items = await TagTransporter.Search(
        pagination,
        search,
      ).then(async ({ items, pagination }) => {
        items.forEach((tag) => {
          this.reinsert(tag);
        });

        // Set new pagination
        newPagination = pagination.deepCopy();;

        return items
      });

      // Set cache
      this.cache[cacheKey] = {
        ids: items.map((x: Tag) => x.id),
        pagination: newPagination.deepCopy(),
        retrievalTimestamp: Date.now(),
      };

      // Set retrieval timestamp
      this.retrievalTimestamp = Date.now();

      return {
        items: items,
        pagination: newPagination,
      };
    },
    async update(tag: Tag): Promise<Tag> {
      // Update tag
      const item = await TagTransporter.Update(tag)

      // Reinsert tag
      this.reinsert(item)

      // Activate tag
      if (this.active === null) {
        await this.activate(item)
      }
      
      this.emitStoreUpdateEvent()

      return item
    },
  },
  getters: {
    active(): Tag | null {
      if (this.activeID === null) {
        return null
      }

      const tag = (this.list as Tag[]).find((x: Tag) => x.id === this.activeID)
      if (tag === undefined) {
        return null
      }

      return tag
    },
    refreshRequired(): boolean {
      return (
        this.retrievalTimestamp === 0 ||
        this.retrievalTimestamp < Date.now() - 30 * 60000 ||
        this.list.length === 0
      )
    },
  },
})

